        private static string SaveImgSignature(string base64String, string username)
        {
            // salvo la signature come immagine e come versione di firma

            int VersioneSignatore = GetLastVersionTecSignature(username);
            string UrlFirma = string.Empty;
            try
            {
                byte[] data = Convert.FromBase64String(base64String);
            }
            catch (Exception ex)
            {
                ErrorLog.SaveErrorLog(ex);
            }
            UrlFirma = "/Images/firma-" + username + ".png";
            string filePath = HttpContext.Current.Server.MapPath("~" + UrlFirma);
            File.WriteAllBytes(filePath, Convert.FromBase64String(base64String));
            SetDbTecSignature(username, VersioneSignatore + 1, base64String, UrlFirma);
            return UrlFirma;
        }

        private static int GetLastVersionTecSignature(string username)
        {
            int _LastVersion = 0;
            using (SqlConnection sqlConnection = WebUtils.GetSqlConnection())
            {
                sqlConnection.Open();
                using (SqlCommand sqlCommand = new SqlCommand("\r\n    SELECT [VersioneFirma] \r\n  FROM [PRT_DipendentiAna]   \r\n    where [UserNameProfileIntra] = @userName ", sqlConnection))
                {
                    sqlCommand.Parameters.AddWithValue(nameof(username), (object)username);
                    using (SqlDataReader sqlDataReader = sqlCommand.ExecuteReader())
                    {
                        while (sqlDataReader.Read())
                        {
                            _LastVersion = Convert.ToInt32(sqlDataReader["VersioneFirma"]);
                        }
                        sqlDataReader.Close();
                    }
                }
                sqlConnection.Close();

            }
            return _LastVersion;
        }

        private static void SetDbTecSignature(string username, int NewVersion, string base64String, string SignatureFilePath)
        {
            Sql4PortalHelper objSqlHelper = new Sql4PortalHelper();
            SqlParameter[] objParams = new SqlParameter[4];

            objParams[0] = new SqlParameter("@UserProfileIntra", username);
            objParams[1] = new SqlParameter("@NewVersion", NewVersion);
            objParams[2] = new SqlParameter("@base64String", base64String);
            objParams[3] = new SqlParameter("@SignatureFilePath", SignatureFilePath);

            objSqlHelper.ExecuteNonQueryForNews("PRT_TecSignature_Update_Def", objParams);
        }
    }

